mapper.select:
select 	model_id,node_id,plugin_type,desc_index,description from  param_flowassigners
where model_id = #{modelId,jdbcType=VARCHAR}
and  node_id = #{nodeId,jdbcType=VARCHAR}
order by create_time

select 	node_id,model_id,plugin_type,desc_index,description  from  param_flowassigners
where model_id = #{modelId,jdbcType=VARCHAR}
order by create_time

select

from param_flowassigners
where model_id = #{modelId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and desc_index = #{descIndex,jdbcType=INTEGER}
  and plugin_type = #{pluginType,jdbcType=VARCHAR}

mapper.delete:
delete from param_flowassigners
where model_id = #{modelId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and desc_index = #{descIndex,jdbcType=INTEGER}
  and plugin_type = #{pluginType,jdbcType=VARCHAR}

mapper.update:
update param_flowassigners


    description = #{description,jdbcType=VARCHAR},


    code_desc = #{codeDesc,jdbcType=VARCHAR},


    create_user_id = #{createUserId,jdbcType=VARCHAR},


    create_time = #{createTime,jdbcType=TIMESTAMP},


    update_user_id = #{updateUserId,jdbcType=VARCHAR},


    update_time = #{updateTime,jdbcType=TIMESTAMP},


where model_id = #{modelId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and desc_index = #{descIndex,jdbcType=INTEGER}
  and plugin_type = #{pluginType,jdbcType=VARCHAR}

update param_flowassigners
set description = #{description,jdbcType=VARCHAR},
  code_desc = #{codeDesc,jdbcType=VARCHAR},
  create_user_id = #{createUserId,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where model_id = #{modelId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and desc_index = #{descIndex,jdbcType=INTEGER}
  and plugin_type = #{pluginType,jdbcType=VARCHAR}
